  #{changeValue,jdbcType=INTEGER}, #{memberId,jdbcType=VARCHAR}, #{accountId,jdbcType=VARCHAR}, 
  #{storeId,jdbcType=VARCHAR}, #{balance,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, 
