mapper.select:
select

  distinct


from gift_card




  order by ${orderByClause}



    limit ${offset}, ${limit}


    limit ${limit}

mapper.insert:
insert into gift_card (gift_card_id, gift_card_pwd, account_id, 
  member_id, value_card_id, money, 
  end_time, `status`, img_url, 
  create_time, cover_id, update_time
  )
values (#{giftCardId,jdbcType=VARCHAR}, #{giftCardPwd,jdbcType=VARCHAR}, #{accountId,jdbcType=VARCHAR}, 
  #{memberId,jdbcType=VARCHAR}, #{valueCardId,jdbcType=VARCHAR}, #{money,jdbcType=INTEGER}, 
  #{endTime,jdbcType=TIMESTAMP}, #{status,jdbcType=INTEGER}, #{imgUrl,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=TIMESTAMP}, #{coverId,jdbcType=INTEGER}, #{updateTime,jdbcType=TIMESTAMP}
  )
